INSERT INTO breweries (brewery_name, contact_info, brewery_history, operating_hours, brewery_img_url, brewery_address, brewery_city, brewery_state, brewery_zip, is_active) VALUES ('Southern Ohio Brewing', 'https://www.southernohiobrewing.com/welcome-91521', 'Family owned and operated, Southern Ohio Brewing is a great place for getting together with family, friends or coworkers. We have 12-14 rotating taps featuring a wide range of  craft beer styles from our on-site brewhouse. Wine and our own house made hard seltzer are also available. ', 'Wed: 4-9pm, Thurs: 4-9pm, Fri: 3-10pm, Sat: 12-10pm, Sun: 12-8pm', 'https://images.squarespace-cdn.com/content/v1/5e8cbbecfb706f4f214ce102/c0376ed6-cf3b-4e49-b238-4977d9a3e5eb/taproom.jpg?format=2500w', '818 Factory Rd', 'Beavercreek', 'OH', 45434, true);
INSERT INTO breweries (brewery_name, contact_info, brewery_history, operating_hours, brewery_img_url, brewery_address, brewery_city, brewery_state, brewery_zip, is_active) VALUES ('Miller Brewing Company', 'dataprotection@molsoncoors.com', 'In 1786, English immigrant John Molson founded Canada’s oldest beer brewery on the banks of the St. Lawrence River in Montreal. He wrote, “My beer has been universally well-liked beyond my most sanguine expectations.” The legacy continues seven generations later with brands like Molson Canadian, which was first brewed in 1959 and remains today one of Canada’s most iconic and best-selling beers.', 'Thurs: 10am-5pm, Fri: 10am-5pm', 'https://www.molsoncoors.com/sites/molsonco/files/2022-05/Brewery_Milwaukee_1920x768_v2.jpg', '4000 W. State Street', 'Milwaukee', 'WI', 53208, true);
